import '../styles/Movies.css';
import {useState} from "react";
function Movies() {
    const date = new Date();
    const currentDate = date.toJSON();
    const currentDateWithoutTime = currentDate.split("T")[0];
    date.setMonth(date.getMonth() + 1);
    const maxPickerDate = date.toJSON().split("T")[0];
    return (
        <div className="movie-repertoire">
            <h1>Repertoire For {currentDateWithoutTime}</h1>
            <input type="date" id="date-picker" name="date-picker" value={currentDateWithoutTime} min={currentDateWithoutTime} max={maxPickerDate}></input>
        </div>
    );
}

export default Movies;
