import '../styles/Movies.css';
import {useEffect, useState} from "react";
function Movies() {
    const [dateIsSelected, setDateIsSelected] = useState(false);
    const date = new Date();
    const currentDate = date.toJSON();
    const currentDateWithoutTime = currentDate.split("T")[0];
    const [datePickerValue, setDatePickerValue] = useState(null);
    date.setMonth(date.getMonth() + 1);
    const maxPickerDate = date.toJSON().split("T")[0];
    const handleChange = (e) => {
        setDatePickerValue(e.target.value);
        if(document.getElementById("date-picker").value !== "" && document.getElementById("date-picker").value !== null) {
            setDateIsSelected(true);
        }
        else {
            setDateIsSelected(false);
        }
    };
    const [movies, setMovies] = useState([]);
    useEffect(() => {
        fetchMovies();
    }, []);
    return (
        <div class="movie-repertoire">
            {
                dateIsSelected ? (
                    <h1 class="title is-2" id="movie-repertoire-h1">Repertoire For: {datePickerValue}</h1>
                ) : (
                    <h1 class="title is-2" id="movie-repertoire-h1">Select Date To Show Repertoire</h1>
                )
            }
            <div class="date-picker">
                <input type="date" className="title" id="date-picker" min={currentDateWithoutTime} max={maxPickerDate} onChange={handleChange}></input>
            </div>
            {
                dateIsSelected ? (
                    <div></div>
                ) : (
                    <div class="tag is-white" id="no-date-selected-info">No date selected</div>
                )
            }
        </div>
    );
}

export default Movies;
