import '../styles/Confirmation.css';
import { reservationCookieName } from './Movies';
import {useEffect, useState} from "react";
import {getCookie} from "../Utilities/cookieUtils";
import {useNavigate} from "react-router-dom";
function Confirmation() {
    const navigate = useNavigate();
    const [cookieContent, setCookieContent] = useState(null);
    const decodedCookieContent = decodeURIComponent(getCookie(reservationCookieName));
    const ticketReservationId = decodedCookieContent;
    const [reservationData, setReservationData] = useState(null);
    useEffect(() => {
        setCookieContent(ticketReservationId);
    }, [ticketReservationId]);
    useEffect(() => {
        const reservationCookie = getCookie(reservationCookieName);
        const decodedCookieContent = decodeURIComponent(reservationCookie);
        const regex = /^(0|[1-9][0-9]*)$/;
        if (!reservationCookie || !regex.test(decodedCookieContent)) {
            navigate('/');
            return; // Stop executing the further "useEffect" code
        }
        const fetchReservation = async () => {
            try {
                const response = await fetch('http://localhost:4000/getTicketReservationById', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ id: cookieContent }),
                });
                const data = await response.json();
                setReservationData(data);
            } catch (err) {
                console.error('Fetch error:', err);
            }
        };
        fetchReservation();
    }, [navigate, cookieContent]);
    return (
        <div className="confirmation">
            <h1 className="title is-2" id="confirmation-h1-title-large">Your Ticket Reservation Is Confirmed</h1>
            <div className="confirmation-space-1"></div>
            <div className="confirmation-detail">Movie: </div>
            <div className="confirmation-detail">Format: </div>
            <div className="confirmation-detail">Time: </div>
            <div className="confirmation-detail">T</div>
            <div className="confirmation-detail">T</div>
            <div className="confirmation-detail">T</div>
            <div className="confirmation-space-2"></div>
        </div>
    );
}

export default Confirmation;
