import '../styles/Movies.css';
import {useState} from "react";
function Movies() {
    const date = new Date();
    const currentDate = date.toJSON();
    const currentDateWithoutTime = currentDate.split("T")[0];
    const [datePickerValue, setDatePickerValue] = useState(currentDateWithoutTime);
    date.setMonth(date.getMonth() + 1);
    const maxPickerDate = date.toJSON().split("T")[0];
    const handleChange = (e) => {
        setDatePickerValue(e.target.value);
    };
    return (
        <div className="movie-repertoire">
            <h1>Repertoire For {datePickerValue}</h1>
            <label for="date-picker" className="calendar-icon">uhiuh
                <input type="date" id="date-picker" name="date-picker" className="hidden-input" min={currentDateWithoutTime} max={maxPickerDate} onChange={handleChange}></input>
            </label>
        </div>
    );
}

export default Movies;
